// import { faClock, faAngleDoubleRight } from "@fortawesome/free-solid-svg-icons";
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React from 'react';
import { Col } from 'react-bootstrap';
import Bounce from 'react-reveal/Bounce';
import { motion } from "framer-motion";
import './LifeAria.css';

const LifeAria = ({news:{ title, time, description, image, url}}) => {

    return (
			<Col md={4} className="p-2">
				<div className="blog-card">
					<div className="blog-content">
						<Bounce bottom cascade>
							<div>
								<img src={image} alt={title} className="img-fluid " />
							</div>
							<div className="p-4">
								<h5 style={{ color: "#fff" }}>{title}</h5>
								<span className="text-info h6">
									{/* <FontAwesomeIcon icon={faClock} /> {time} */}
								</span>
								<p className="text-light pt-3">{description}</p>
							</div>
							<div>
								<a href={url} className="ml-3" rel="noreferrer" target="_blank">
									<motion.button
										whileHover={{
											scale: 1.1,
											textShadow: "0px 0px 8px rgb(255 255 255)",
											boxShadow: "0px 0px 8px #17a2b8",
											transition: { duration: 0.4, yoyo: "Infinity" },
										}}
										className="news-button mb-3"
									>
										Read More <FontAwesomeIcon icon={faAngleDoubleRight} />
									</motion.button>
								</a>
							</div>
						</Bounce>
					</div>
				</div>
			</Col>
		);
};

export default LifeAria;







































