import React from 'react';
import Footer from './Footer/Footer';
import { Col, Container, Image, Row } from "react-bootstrap";
import { MdContactMail } from 'react-icons/md';
import { Link } from "react-router-dom";
import aboutImage from "../img/grayson.png";
import Navbarmenu from './menu/Navbarmenu';

const About = () => {
       return (
              <div>
                     <Navbarmenu/>
                     <section>
			<Container>
				<Row className="align-items-center justify-content-center banner">
					<Col md={6}>
						<h1><span className>HI</span> I'm Dr. Michael C. Grayson!</h1>
						<div className="mb-3 font-italic  banner-subtitle">
						</div>
						<Link smooth as={Link} to="/contactMe" className="ml-3">
							<button
								whileHover={{
									scale: 1.1,
									textShadow: "0px 0px 8px rgb(255 255 255)",
									boxShadow: "0px 0px 8px #17a2b8",
									transition: { duration: 0.4, yoyo: "Infinity" },
								}}
								className="main-button"
							>
								<MdContactMail className="download-icon" />
								Contact
							</button>
						</Link>
					</Col>
					<Col className="offset-md-1" md={6}>
						<Image
							className="img-fluid mt-3"
							src={aboutImage}
							alt="..."
						/>
					</Col>
				</Row>
			</Container>
		</section>
                     <Footer/>
              </div>
       )
}

export default About
